import React from "react";
import {StyleSheet, Text, View} from "react-native";


export const MovieDetailed = ({navigation, route}) => {

    const props = route.params

    return (
        <View style={styles.moviesStyle}>
            <View style={styles.movies}>
                <Text style={styles.id}>Movie Number: {props.id}</Text>
                <Text style={styles.title}>Title: {props.title}</Text>
                <Text style={styles.title}>{props.overview}</Text>
                <Text style={styles.rate}>{props.rate}</Text>
                <Text style={styles.genre}>{props.trailer}</Text>
                <Text style={styles.genre}>{props.image}</Text>
                <Text style={styles.genre}>{props.language}</Text>
                <Text style={styles.genre}>{props.duration} min.</Text>
                <Text style={styles.genre}>{props.releaseDate}</Text>
                <Text style={styles.genre}>{props.adult}</Text>
                <Text style={styles.genre}>{props.genre}</Text>
            </View>
        </View>

    );

};

const styles = StyleSheet.create({
    movies: {
        flex:1,
        flexDirection: "row",
        paddingTop: 10,

    },

    id: {
        marginRight: 20,
        fontSize: 25,
    },
    title: {
        flex: 1,
        marginRight: 20,
        fontSize: 25,
        fontWeight: "bold",
    },
    genre: {
        marginRight: 20,
        fontSize: 25,
    },
    duration: {
        marginRight: 20,
        fontSize: 25,
    },
    language: {
        marginRight: 20,
        fontSize: 25,
    },
    rate: {
        marginRight: 20,
        fontSize: 25,
    },

    button: {
        marginRight: 10,
    },
});
