import { useState } from "react";
import React from 'react';
import { StyleSheet, Text, View, TextInput, Button, SafeAreaView } from 'react-native';

import axios from 'axios';

export default function RegisterMovie() {

    const [title, setTitle] = useState("");
    const [overview, setOverview] = useState("");
    const [rate, setRate] = useState(0);
    const [trailer, setTrailer] = useState("");
    const [image, setImage] = useState("");
    const [releaseDate, setReleaseDate] = useState("");
    const [language, setLanguage] = useState("");
    const [duration, setDuration] = useState(0);
    const [adult, setAdult] = useState("");
    const [genre, setGenre] = useState("");

    const onSubmit = () => {
        const data = {
            title: title,
            overview: overview,
            rate: rate,
            trailer: trailer,
            image: image,
            releaseDate: new Date(releaseDate),
            language: language,
            duration: duration,
            adult: adult,
            genre: genre,
        }

        if(!(title === "") && !(rate == null)) {
            axios.post("http://192.168.1.95:8080/movies", data);
            alert("Movie saved successfully!")
        }else{
            alert("Movie saving failed, both fields must be complete!")
        }
    }

    return (
        <SafeAreaView>
            <View style={styles.container}>
                <View>
                    <Text style={{
                        marginVertical: 30,
                        fontSize: 25,
                    }}>Add new movie</Text>
                </View>
                <TextInput
                    style={styles.txtInput}
                    onChangeText={setTitle}
                    value={title}
                    placeholder='Title'
                />

                <TextInput
                    style={styles.txtInput}
                    onChangeText={setOverview}
                    value={overview}
                    placeholder='Overview'
                />

                <TextInput
                    style={styles.txtInput}
                    onChangeText={setRate}
                    value={rate}
                    placeholder='Rate'
                    keyboardType="numeric"
                />

                <TextInput
                    style={styles.txtInput}
                    onChangeText={setTrailer}
                    value={trailer}
                    placeholder='Trailer-URL'
                />

                <TextInput
                    style={styles.txtInput}
                    value={releaseDate}
                    onChangeText={setReleaseDate}
                    placeholder='YYYY-MM-DD'
                />

                <TextInput
                    style={styles.txtInput}
                    onChangeText={setImage}
                    value={image}
                    placeholder='Image-URL'
                />

                <TextInput
                    style={styles.txtInput}
                    onChangeText={setLanguage}
                    value={language}
                    placeholder='Language'
                />
                <TextInput
                    style={styles.txtInput}
                    onChangeText={setDuration}
                    value={duration}
                    placeholder='Duration / min'
                    keyboardType="numeric"
                />
                <TextInput
                    style={styles.txtInput}
                    onChangeText={setAdult}
                    value={adult}
                    placeholder='Adult'
                />
                <TextInput
                    style={styles.txtInput}
                    onChangeText={setGenre}
                    value={genre}
                    placeholder='Genre'
                />

                <Button
                    onPress={onSubmit}
                    style={styles.button}
                    title="Submit"
                />
            </View>
            <View>
                <Text> </Text>
                <Text> </Text>
            </View>

        </SafeAreaView>

    );
};

const styles = StyleSheet.create(
    {

        container:{
            flex: 1,
            alignItems: 'center',
            marginTop: 10,
        },

        regForm: {
            alignSelf: 'stretch',
        },

        header: {
            fontsize: 24,
            paddingBottom: 10,
            marginBottom: 40,
            borderBottomColor: '#42CCD8',
            borderBottomWidth: 1,
        },

        txtInput: {
            fontsize: 24,
            paddingBottom: 10,
            marginBottom: 40,
            height: 35,
            paddingHorizontal: 5,
            borderBottomColor: '#42CCD8',
            borderBottomWidth: 1,
            backgroundColor: "white",
            opacity: 0.6,
            borderWidth: 1,
            borderRadius: 5,
        },

        button: {
            alignSelf: 'stretch',
            alignItems: 'center',
            padding: 20,
            backgroundColor: '#42CCD8',
            margin: 40,
            borderRadius: 10,
        },

    }
);
