import React from "react";
import {StyleSheet, Text, View} from "react-native";


export const MovieDetailed = ({navigation, route}) => {

    const props = route.params

    return (
        <View style={styles.moviesStyle}>
            <View style={styles.movies}>
                <Text style={styles.id}>Movie Number: {props.id}</Text>
                <Text style={styles.title}>Title: {props.title}</Text>
                <Text style={styles.title}>Overview: {props.overview}</Text>
                <Text style={styles.rate}>rate: {props.rate}</Text>
                <Text style={styles.genre}>trailer: {props.trailer}</Text>
                <Text style={styles.genre}>image: {props.image}</Text>
                <Text style={styles.genre}>language: {props.language}</Text>
                <Text style={styles.genre}>duration: {props.duration} min.</Text>
                <Text style={styles.genre}>releaseDate: {props.releaseDate}</Text>
                <Text style={styles.genre}>adult: {props.adult}</Text>
                <Text style={styles.genre}>genre: {props.genre}</Text>
            </View>
        </View>

    );

};

const styles = StyleSheet.create({
    moviesStyle: {
        flex:1,
    },
    movies: {
        flexDirection: "column",
        paddingTop: 10,

    },

    id: {
        marginRight: 20,
        fontSize: 25,
    },
    genre: {
        marginRight: 20,
        fontSize: 25,
    },
    duration: {
        marginRight: 20,
        fontSize: 25,
    },
    language: {
        marginRight: 20,
        fontSize: 25,
    },
    rate: {
        marginRight: 20,
        fontSize: 25,
    },

    button: {
        marginRight: 10,
    },
});
