import {useState} from "react";
import React from 'react';
import {StyleSheet, TextInput, Button, SafeAreaView, Text, View} from 'react-native';

import axios from 'axios';

export default function RegisterDirector() {

    const [name, setName] = useState("");
    const [lastName, setLastName] = useState("");

    const onSubmit = () => {

        const data = {
            name: name,
            lastName: lastName,
        }

        if(!(data.name === "") && !(data.lastName === "")) {
            axios.post("http://192.168.1.95:8080/directors", data);
            alert("Director saved successfully!")
        }else{
            alert("Director saving failed, both fields must be complete!")
        }

    }


    return (
        <SafeAreaView style={styles.container}>
            <View style={styles.title}>
                <Text>Register new director</Text>
            </View>

            <TextInput
                style={styles.txtInput}
                onChangeText={setName}
                value={name}
                placeholder='Name'
            />

            <TextInput
                style={styles.txtInput}
                onChangeText={setLastName}
                value={lastName}
                placeholder='Last Name'
            />

            <View style={styles.buttonSubmit}>
                <Button
                    onPress={onSubmit}
                    title="Submit"
                />

            </View>

        </SafeAreaView>
    );
};

const styles = StyleSheet.create(
    {
        container: {

        },
        regForm: {
            alignSelf: 'stretch',
        },

        title: {
            fontsize: 24,
            paddingBottom: 30,
        },

        header: {
            fontsize: 24,
            paddingBottom: 10,
            marginBottom: 40,
            borderBottomColor: '#42CCD8',
            borderBottomWidth: 1,
        },

        txtInput: {
            fontsize: 24,
            paddingBottom: 10,
            marginBottom: 40,
            height: 35,
            paddingHorizontal: 5,
            borderBottomColor: '#42CCD8',
            borderBottomWidth: 1,
            backgroundColor: "white",
            opacity: 0.6,
            borderWidth: 1,
            borderRadius: 5,
        },

        buttonSubmit: {
            marginTop: 10,
            opacity: 0.8,
        },

    }
);
