import React from "react";
import {StyleSheet, Text, View} from "react-native";


export const MovieDetailed = ({ navigation, route }) => {

    const props = route.params

    return (
        <View style={styles.moviesStyle}>

            <Text style={styles.id}>{props.id}</Text>
            <Text style={styles.title}>{props.title}</Text>
            <Text style={styles.title}>{props.overview}</Text>
            <Text style={styles.rate}>{props.rate}</Text>
            <Text style={styles.genre}>{props.trailer}</Text>
            <Text style={styles.genre}>{props.image}</Text>
            <Text style={styles.genre}>{props.language}</Text>
            <Text style={styles.genre}>{props.duration} min.</Text>
            <Text style={styles.genre}>{props.duration} min.</Text>
            

            <Text style={styles.genre}>{props.genre}</Text>



            <Text style={styles.rate}>{props.actors}</Text>

        </View>
    );

};

const styles = StyleSheet.create({
    moviesStyle: {
        flex: 1,
        flexDirection: "row",
        paddingTop: 10,

    },

    id: {
        marginRight: 20,
        fontSize: 25,
    },
    title: {
        marginRight: 20,
        fontSize: 25,
        fontWeight: "bold",
    },
    genre: {
        marginRight: 20,
        fontSize: 25,
    },
    duration: {
        marginRight: 20,
        fontSize: 25,
    },
    language: {
        marginRight: 20,
        fontSize: 25,
    },
    rate: {
        marginRight: 20,
        fontSize: 25,
    },

    button: {
        marginRight: 10,
    },
});
